"use client";

import { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import { useTheme } from "../context/ThemeContext";
import apiService from "../services/api";
import TaskModal from "./TaskModal";
import UserModal from "./UserModal";
import TaskAssignModal from "./TaskAssignModal";
import SubAdminModal from "./SubAdminModal";
import PermissionModal from "./PermissionModal";
import EmailModal from "./EmailModal";
import ErrorNotification from "./ErrorNotification";
import { format } from "date-fns";

import {
  Box,
  Typography,
  Button,
  Card,
  CardContent,
  CircularProgress,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  IconButton,
  Chip,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  TableContainer,
} from "@mui/material";
import {
  Home as HomeIcon,
  AccessTime as ClockIcon,
  CheckCircleOutline as CheckCircleIcon,
  BarChart as BarChartIcon,
  Group as UsersIcon,
  PersonAdd as UserPlusIcon,
  PersonRemove as UserXIcon,
  Edit as EditIcon,
  Delete as Trash2Icon,
  Menu as MenuIcon,
  Close as CloseIcon,
  Logout as LogoutIcon,
  PersonCheck as UserCheckIcon,
  PersonOutline as UserMinusIcon,
  WbSunny as SunIcon,
  DarkMode as MoonIcon,
  Settings as SettingsIcon,
  MailOutline as MailIcon,
  AddCircleOutline as PlusCircleIcon,
} from "@mui/icons-material";

const AdminDashboard = () => {
  const { user, logout } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const [tasks, setTasks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [showUserMenu, setShowUserMenu] = useState(false);
  const [error, setError] = useState(null);

  // Modals
  const [showTaskModal, setShowTaskModal] = useState(false); // For editing existing tasks
  const [selectedTask, setSelectedTask] = useState(null);
  const [showUserModal, setShowUserModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showTaskAssignModal, setShowTaskAssignModal] = useState(false); // For assigning NEW tasks from sidebar
  const [showSubAdminModal, setShowSubAdminModal] = useState(false);
  const [subAdminToEdit, setSubAdminToEdit] = useState(null);
  const [showPermissionModal, setShowPermissionModal] = useState(false);
  const [userToManagePermissions, setUserToManagePermissions] = useState(null);
  const [showEmailModal, setShowEmailModal] = useState(false);
  const [usersToEmail, setUsersToEmail] = useState([]);

  // View states
  const [activeView, setActiveView] = useState("overview"); // overview, manage-tasks, manage-users, unassigned-users
  const [activeTimeline, setActiveTimeline] = useState("all"); // all, today, week, month, completed, pending, assigned, deadline-missed

  useEffect(() => {
    if (user) {
      fetchData();
    }
  }, [user]);

  const fetchData = async () => {
    setLoading(true);
    try {
      const [tasksResponse, usersResponse] = await Promise.all([
        apiService.getAllTasksAdmin(),
        apiService.getAllUsers(),
      ]);

      if (tasksResponse.success) {
        setTasks(tasksResponse.tasks);
      } else {
        setError(tasksResponse.message || "Failed to fetch tasks");
      }

      if (usersResponse.success) {
        setUsers(usersResponse.users);
      } else {
        setError(usersResponse.message || "Failed to fetch users");
      }
      setError(null);
    } catch (err) {
      console.error("Error fetching data:", err);
      setError(err.message || "An unexpected error occurred.");
    } finally {
      setLoading(false);
    }
  };

  // This function will be used by TaskModal for creating/updating tasks
  const handleSaveTask = async (taskData, taskId = null) => {
    try {
      let response;
      if (taskId) {
        response = await apiService.updateTaskAdmin(taskId, taskData);
      } else {
        response = await apiService.createTaskAdmin(taskData);
      }

      if (response.success && response.task) {
        if (taskId) {
          setTasks(
            tasks.map((task) => (task._id === taskId ? response.task : task))
          );
        } else {
          setTasks([...tasks, response.task]);
        }
        setShowTaskModal(false);
        setSelectedTask(null);
        setError(null);
      } else {
        setError(
          response.message || `Failed to ${taskId ? "update" : "create"} task`
        );
      }
    } catch (err) {
      setError(err.message);
    }
  };

  // This function will be used by TaskAssignModal for creating and assigning NEW tasks
  const handleCreateAndAssignTask = async (taskData) => {
    try {
      const response = await apiService.createTaskAdmin(taskData); // createTask now handles assignment
      if (response.success && response.task) {
        setTasks([...tasks, response.task]);
        setShowTaskAssignModal(false);
        setError(null);
      } else {
        setError(response.message || "Failed to create and assign task");
      }
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDeleteTask = async (taskId) => {
    if (window.confirm("Are you sure you want to delete this task?")) {
      try {
        const response = await apiService.deleteTaskAdmin(taskId);
        if (response.success) {
          setTasks(tasks.filter((task) => task._id !== taskId));
          setShowTaskModal(false);
          setSelectedTask(null);
          setError(null);
        } else {
          setError(response.message || "Failed to delete task");
        }
      } catch (err) {
        setError(err.message);
      }
    }
  };

  const handleCreateUser = async (userData) => {
    try {
      const response = await apiService.register(userData);
      if (response.success && response.user) {
        setUsers([...users, response.user]);
        setShowUserModal(false);
        setError(null);
      } else {
        setError(response.message || "Failed to create user");
      }
    } catch (err) {
      setError(err.message);
    }
  };

  const handleUpdateUser = async (userId, userData) => {
    try {
      const response = await apiService.updateUser(userId, userData);
      if (response.success && response.user) {
        setUsers(users.map((u) => (u._id === userId ? response.user : u)));
        setShowUserModal(false);
        setSelectedUser(null);
        setError(null);
      } else {
        setError(response.message || "Failed to update user");
      }
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDeleteUser = async (userId) => {
    if (window.confirm("Are you sure you want to delete this user?")) {
      try {
        const response = await apiService.deleteUser(userId);
        if (response.success) {
          setUsers(users.filter((u) => u._id !== userId));
          setShowUserModal(false);
          setSelectedUser(null);
          setError(null);
        } else {
          setError(response.message || "Failed to delete user");
        }
      } catch (err) {
        setError(err.message);
      }
    }
  };

  const handleRemoveTaskAssignment = async (taskId) => {
    if (window.confirm("Are you sure you want to unassign this task?")) {
      try {
        const response = await apiService.unassignTaskAdmin(taskId);
        if (response.success && response.task) {
          setTasks(
            tasks.map((task) => (task._id === taskId ? response.task : task))
          );
          setError(null);
        } else {
          setError(response.message || "Failed to unassign task");
        }
      } catch (err) {
        setError(err.message);
      }
    }
  };

  // Modified: handleCreateSubAdmin now promotes an existing user
  const handlePromoteToSubAdmin = async (userId) => {
    try {
      const response = await apiService.promoteToSubAdmin(userId);
      if (response.success && response.user) {
        setUsers(users.map((u) => (u._id === userId ? response.user : u)));
        setShowSubAdminModal(false);
        setError(null);
      } else {
        setError(response.message || "Failed to promote user to sub-admin");
      }
    } catch (err) {
      setError(err.message);
    }
  };

  // New: handle demote sub-admin
  const handleDemoteSubAdmin = async (userId) => {
    if (
      window.confirm(
        "Are you sure you want to demote this sub-admin to a regular user?"
      )
    ) {
      try {
        const response = await apiService.demoteSubAdmin(userId);
        if (response.success && response.user) {
          setUsers(users.map((u) => (u._id === userId ? response.user : u)));
          setError(null);
        } else {
          setError(response.message || "Failed to demote sub-admin");
        }
      } catch (err) {
        setError(err.message);
      }
    }
  };

  const handleUpdatePermissions = async (userId, permissions) => {
    try {
      const response = await apiService.updateSubAdminPermissions(
        userId,
        permissions
      );
      if (response.success && response.user) {
        setUsers(users.map((u) => (u._id === userId ? response.user : u)));
        setShowPermissionModal(false);
        setUserToManagePermissions(null);
        setError(null);
      } else {
        setError(response.message || "Failed to update permissions");
      }
    } catch (err) {
      setError(err.message);
    }
  };

  const handleSendEmail = async (recipients, subject, message) => {
    try {
      const response = await apiService.sendEmail(recipients, subject, message);
      if (response.success) {
        setError("Email sent successfully!");
        setShowEmailModal(false);
        setUsersToEmail([]);
      } else {
        setError(response.message || "Failed to send email");
      }
    } catch (err) {
      setError(err.message || "An error occurred while sending email.");
    }
  };

  const handleLogout = () => {
    logout();
    setShowUserMenu(false);
  };

  const getStats = () => {
    const totalTasks = tasks.length;
    const completedTasks = tasks.filter((task) => task.completed).length;
    const pendingTasks = totalTasks - completedTasks;
    const totalUsers = users.length;
    const unassignedUsers = users.filter(
      (u) =>
        u.role === "user" &&
        !tasks.some((task) => task.assignedTo?._id === u._id)
    ).length;
    return {
      totalTasks,
      completedTasks,
      pendingTasks,
      totalUsers,
      unassignedUsers,
    };
  };

  const stats = getStats();

  const getTimelineData = () => {
    let filtered = tasks;
    if (activeTimeline === "today") {
      filtered = tasks.filter((task) => {
        if (!task.dueDate) return false;
        return (
          new Date(task.dueDate).toDateString() === new Date().toDateString()
        );
      });
    } else if (activeTimeline === "week") {
      filtered = tasks.filter((task) => {
        if (!task.dueDate) return false;
        const oneWeek = new Date();
        oneWeek.setDate(oneWeek.getDate() + 7);
        return new Date(task.dueDate) <= oneWeek;
      });
    } else if (activeTimeline === "month") {
      filtered = tasks.filter((task) => {
        if (!task.dueDate) return false;
        const oneMonth = new Date();
        oneMonth.setMonth(oneMonth.getMonth() + 1);
        return new Date(task.dueDate) <= oneMonth;
      });
    } else if (activeTimeline === "completed") {
      filtered = tasks.filter((task) => task.completed);
    } else if (activeTimeline === "pending") {
      filtered = tasks.filter((task) => !task.completed);
    } else if (activeTimeline === "assigned") {
      filtered = tasks.filter((task) => task.assignedTo);
    } else if (activeTimeline === "deadline-missed") {
      const now = new Date();
      filtered = tasks.filter((task) => {
        if (!task.dueDate) return false;
        const dueDate = new Date(task.dueDate);
        return dueDate < now && !task.completed;
      });
    }
    return filtered;
  };

  const handleTimelineClick = (type) => {
    setActiveTimeline(type);
  };

  const getFilteredUsers = () => {
    if (activeView === "unassigned-users") {
      const assignedUserIds = new Set(
        tasks
          .filter((task) => task.assignedTo)
          .map((task) => task.assignedTo._id)
      );
      return users.filter(
        (u) => u.role === "user" && !assignedUserIds.has(u._id)
      );
    }
    return users;
  };

  if (loading) {
    return (
      <Box
        className="min-h-screen flex items-center justify-center"
        sx={{ backgroundColor: "background.default" }}
      >
        <Box className="text-center">
          <CircularProgress size={80} thickness={4} color="primary" />
          <Typography variant="h6" sx={{ mt: 2, color: "text.secondary" }}>
            Loading admin dashboard...
          </Typography>
        </Box>
      </Box>
    );
  }

  return (
    <Box
      className="min-h-screen flex"
      sx={{ backgroundColor: "background.default" }}
    >
      {/* Error Notification */}
      {error && (
        <ErrorNotification message={error} onClose={() => setError(null)} />
      )}

      {/* Mobile Sidebar Overlay */}
      {sidebarOpen && (
        <Box
          className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Sidebar */}
      <Box
        className={`fixed lg:static inset-y-0 left-0 z-50 w-64 bg-white dark:bg-gray-800 shadow-sm border-r border-gray-200 dark:border-gray-700 transform ${
          sidebarOpen ? "translate-x-0" : "-translate-x-full"
        } lg:translate-x-0 transition-transform duration-300 ease-in-out`}
      >
        <Box className="p-6">
          <Box className="flex items-center justify-between mb-8">
            <Box className="flex items-center space-x-2">
              <Box className="w-8 h-8 bg-gradient-to-r from-purple-500 to-pink-500 rounded-lg flex items-center justify-center">
                <Typography variant="button" className="text-white font-bold">
                  T
                </Typography>
              </Box>
              <Typography
                variant="h6"
                className="font-bold text-gray-800 dark:text-gray-100"
              >
                TaskFlow
              </Typography>
            </Box>
            <IconButton
              onClick={() => setSidebarOpen(false)}
              className="lg:hidden text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200"
            >
              <CloseIcon />
            </IconButton>
          </Box>
          <Box className="mb-6">
            <Box className="flex items-center space-x-3 mb-2">
              <Box className="w-10 h-10 bg-purple-500 rounded-full flex items-center justify-center">
                <Typography
                  variant="button"
                  className="text-white font-semibold"
                >
                  {user?.name?.charAt(0)}
                </Typography>
              </Box>
              <Box>
                <Typography
                  variant="subtitle1"
                  className="font-medium text-gray-800 dark:text-gray-100"
                >
                  {user?.name}
                </Typography>
                <Typography variant="body2" className="text-sm text-purple-500">
                  Admin
                </Typography>
              </Box>
            </Box>
          </Box>
          <List component="nav" className="space-y-2">
            <ListItem
              button
              onClick={() => setActiveView("overview")}
              className={`${
                activeView === "overview"
                  ? "bg-purple-50 text-purple-600 dark:bg-purple-900 dark:text-purple-200"
                  : "text-gray-600 hover:bg-gray-50 dark:text-gray-300 dark:hover:bg-gray-700"
              }`}
            >
              <ListItemIcon sx={{ minWidth: 40 }}>
                <HomeIcon
                  fontSize="small"
                  className={`${
                    activeView === "overview"
                      ? "text-purple-600 dark:text-purple-200"
                      : "text-gray-600 dark:text-gray-300"
                  }`}
                />
              </ListItemIcon>
              <ListItemText primary="Overview" />
            </ListItem>
            <ListItem
              button
              onClick={() => setActiveView("manage-tasks")}
              className={`${
                activeView === "manage-tasks"
                  ? "bg-purple-50 text-purple-600 dark:bg-purple-900 dark:text-purple-200"
                  : "text-gray-600 hover:bg-gray-50 dark:text-gray-300 dark:hover:bg-gray-700"
              }`}
            >
              <ListItemIcon sx={{ minWidth: 40 }}>
                <BarChartIcon
                  fontSize="small"
                  className={`${
                    activeView === "manage-tasks"
                      ? "text-purple-600 dark:text-purple-200"
                      : "text-gray-600 dark:text-gray-300"
                  }`}
                />
              </ListItemIcon>
              <ListItemText primary="Manage Tasks" />
            </ListItem>
            <ListItem
              button
              onClick={() => setActiveView("manage-users")}
              className={`${
                activeView === "manage-users"
                  ? "bg-purple-50 text-purple-600 dark:bg-purple-900 dark:text-purple-200"
                  : "text-gray-600 hover:bg-gray-50 dark:text-gray-300 dark:hover:bg-gray-700"
              }`}
            >
              <ListItemIcon sx={{ minWidth: 40 }}>
                <UsersIcon
                  fontSize="small"
                  className={`${
                    activeView === "manage-users"
                      ? "text-purple-600 dark:text-purple-200"
                      : "text-gray-600 dark:text-gray-300"
                  }`}
                />
              </ListItemIcon>
              <ListItemText primary="Manage Users" />
            </ListItem>
            {/* New: Assign New Task button in sidebar */}
            <ListItem
              button
              onClick={() => setShowTaskAssignModal(true)}
              className={`${
                showTaskAssignModal
                  ? "bg-purple-50 text-purple-600 dark:bg-purple-900 dark:text-purple-200"
                  : "text-gray-600 hover:bg-gray-50 dark:text-gray-300 dark:hover:bg-gray-700"
              }`}
            >
              <ListItemIcon sx={{ minWidth: 40 }}>
                <PlusCircleIcon
                  fontSize="small"
                  className={`${
                    showTaskAssignModal
                      ? "text-purple-600 dark:text-purple-200"
                      : "text-gray-600 dark:text-gray-300"
                  }`}
                />
              </ListItemIcon>
              <ListItemText primary="Assign New Task" />
            </ListItem>
          </List>
          <Box sx={{ mt: 4 }}>
            <Button
              fullWidth
              onClick={toggleTheme}
              startIcon={theme === "light" ? <MoonIcon /> : <SunIcon />}
              sx={{
                justifyContent: "flex-start",
                color: "text.secondary",
                "&:hover": {
                  backgroundColor: "action.hover",
                },
              }}
            >
              {theme === "light" ? "Dark Mode" : "Light Mode"}
            </Button>
          </Box>
          <Box sx={{ mt: 1 }}>
            <Button
              fullWidth
              onClick={handleLogout}
              startIcon={<LogoutIcon />}
              sx={{
                justifyContent: "flex-start",
                color: "error.main",
                "&:hover": {
                  backgroundColor: "error.light",
                  color: "error.contrastText",
                },
              }}
            >
              Logout
            </Button>
          </Box>
        </Box>
      </Box>

      {/* Main Content */}
      <Box className="flex-1 p-6">
        {/* Mobile Header */}
        <Box className="flex items-center justify-between mb-6 lg:hidden">
          <IconButton
            onClick={() => setSidebarOpen(true)}
            className="text-gray-600 hover:text-gray-800 dark:text-gray-300 dark:hover:text-gray-100"
          >
            <MenuIcon />
          </IconButton>
          <Typography
            variant="h6"
            className="font-bold text-gray-800 dark:text-gray-100"
          >
            Admin Dashboard
          </Typography>
          <Box sx={{ width: 24 }} /> {/* Placeholder for alignment */}
        </Box>

        {/* Overview Content */}
        {activeView === "overview" && (
          <Box className="space-y-6">
            <Typography
              variant="h5"
              className="font-bold text-gray-800 dark:text-gray-100"
            >
              Overview
            </Typography>
            {/* Stats Cards - Redefined Order */}
            <Box className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
              {/* Total Tasks */}
              <Card
                onClick={() => setActiveView("manage-tasks")}
                className="cursor-pointer transition-all hover:shadow-md"
                sx={{ border: 1, borderColor: "divider" }}
              >
                <CardContent>
                  <Box className="flex items-center justify-between">
                    <Box>
                      <Typography
                        variant="h5"
                        className="font-bold text-gray-800 dark:text-gray-100"
                      >
                        {stats.totalTasks}
                      </Typography>
                      <Typography
                        variant="body2"
                        className="text-gray-600 dark:text-gray-300"
                      >
                        Total Tasks
                      </Typography>
                    </Box>
                    <BarChartIcon color="primary" />
                  </Box>
                </CardContent>
              </Card>
              {/* Pending Tasks */}
              <Card
                onClick={() => handleTimelineClick("pending")}
                className={`cursor-pointer transition-all hover:shadow-md ${
                  activeTimeline === "pending"
                    ? "border-orange-500 bg-orange-50 dark:bg-orange-900"
                    : "border-gray-200 dark:border-gray-700"
                }`}
                sx={{ border: 1 }}
              >
                <CardContent>
                  <Box className="flex items-center justify-between">
                    <Box>
                      <Typography
                        variant="h5"
                        className="font-bold text-orange-600 dark:text-orange-400"
                      >
                        {stats.pendingTasks}
                      </Typography>
                      <Typography
                        variant="body2"
                        className="text-gray-600 dark:text-gray-300"
                      >
                        Pending Tasks
                      </Typography>
                    </Box>
                    <ClockIcon sx={{ color: "orange.main" }} />
                  </Box>
                </CardContent>
              </Card>
              {/* Completed Tasks */}
              <Card
                onClick={() => handleTimelineClick("completed")}
                className={`cursor-pointer transition-all hover:shadow-md ${
                  activeTimeline === "completed"
                    ? "border-green-500 bg-green-50 dark:bg-green-900"
                    : "border-gray-200 dark:border-gray-700"
                }`}
                sx={{ border: 1 }}
              >
                <CardContent>
                  <Box className="flex items-center justify-between">
                    <Box>
                      <Typography
                        variant="h5"
                        className="font-bold text-green-600 dark:text-green-400"
                      >
                        {stats.completedTasks}
                      </Typography>
                      <Typography
                        variant="body2"
                        className="text-gray-600 dark:text-gray-300"
                      >
                        Completed Tasks
                      </Typography>
                    </Box>
                    <CheckCircleIcon sx={{ color: "green.main" }} />
                  </Box>
                </CardContent>
              </Card>
              {/* Total Users */}
              <Card
                onClick={() => setActiveView("manage-users")}
                className="cursor-pointer transition-all hover:shadow-md"
                sx={{ border: 1, borderColor: "divider" }}
              >
                <CardContent>
                  <Box className="flex items-center justify-between">
                    <Box>
                      <Typography
                        variant="h5"
                        className="font-bold text-blue-600 dark:text-blue-400"
                      >
                        {stats.totalUsers}
                      </Typography>
                      <Typography
                        variant="body2"
                        className="text-gray-600 dark:text-gray-300"
                      >
                        Total Users
                      </Typography>
                    </Box>
                    <UsersIcon sx={{ color: "blue.main" }} />
                  </Box>
                </CardContent>
              </Card>
              {/* Unassigned Users */}
              <Card
                onClick={() => setActiveView("unassigned-users")}
                className="cursor-pointer transition-all hover:shadow-md"
                sx={{ border: 1, borderColor: "divider" }}
              >
                <CardContent>
                  <Box className="flex items-center justify-between">
                    <Box>
                      <Typography
                        variant="h5"
                        className="font-bold text-red-600 dark:text-red-400"
                      >
                        {stats.unassignedUsers}
                      </Typography>
                      <Typography
                        variant="body2"
                        className="text-gray-600 dark:text-gray-300"
                      >
                        Unassigned Users
                      </Typography>
                    </Box>
                    <UserXIcon sx={{ color: "red.main" }} />
                  </Box>
                </CardContent>
              </Card>
            </Box>
            {/* Timeline Bar */}
            <Card className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700">
              <Box className="flex items-center justify-between mb-4">
                <Typography
                  variant="h6"
                  className="font-semibold text-gray-800 dark:text-gray-100"
                >
                  {activeTimeline === "all" && "All Tasks Timeline"}
                  {activeTimeline === "today" && "Tasks Due Today"}
                  {activeTimeline === "week" && "Tasks Due This Week"}
                  {activeTimeline === "month" && "Tasks Due This Month"}
                  {activeTimeline === "completed" && "Completed Tasks Timeline"}
                  {activeTimeline === "pending" && "Pending Tasks Timeline"}
                  {activeTimeline === "assigned" && "Assigned Tasks Timeline"}
                  {activeTimeline === "deadline-missed" &&
                    "Deadline Missed Tasks"}
                </Typography>
                <Typography
                  variant="body2"
                  className="text-sm text-gray-500 dark:text-gray-400"
                >
                  {getTimelineData().length} items
                </Typography>
              </Box>
              <Box className="space-y-3 max-h-96 overflow-y-auto">
                {getTimelineData().map((task, index) => (
                  <Box
                    key={task._id}
                    onClick={() => {
                      setSelectedTask(task);
                      setShowTaskModal(true);
                    }}
                    className="flex items-center justify-between p-3 bg-gray-50 dark:bg-gray-700 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-600 cursor-pointer transition-colors"
                  >
                    <Box className="flex items-center space-x-3">
                      <Box className="w-10 h-10 bg-purple-500 rounded-full flex items-center justify-center">
                        <Typography
                          variant="button"
                          className="text-white text-sm font-semibold"
                        >
                          {index + 1}
                        </Typography>
                      </Box>
                      <Box className="flex-1">
                        <Typography
                          variant="subtitle1"
                          className="font-medium text-gray-800 dark:text-gray-100"
                        >
                          {task.title}
                        </Typography>
                        <Typography
                          variant="body2"
                          className="text-sm text-gray-600 dark:text-gray-300"
                        >
                          Assigned to: {task.assignedTo?.name || "Unassigned"}
                        </Typography>
                        {task.dueDate && (
                          <Typography
                            variant="caption"
                            className="text-xs text-gray-500 dark:text-gray-400"
                          >
                            Due: {new Date(task.dueDate).toLocaleDateString()}
                          </Typography>
                        )}
                      </Box>
                    </Box>
                    <Box className="flex flex-col space-y-1">
                      <Chip
                        label={task.priority || "Low"}
                        size="small"
                        className={`px-2 py-1 rounded-full text-xs font-medium ${
                          task.priority === "High"
                            ? "bg-red-100 text-red-600 dark:bg-red-800 dark:text-red-100"
                            : task.priority === "Medium"
                            ? "bg-yellow-100 text-yellow-600 dark:bg-yellow-800 dark:text-yellow-100"
                            : "bg-green-100 text-green-600 dark:bg-green-800 dark:text-green-100"
                        }`}
                      />
                      <Chip
                        label={task.completed ? "Completed" : "Pending"}
                        size="small"
                        className={`px-2 py-1 rounded-full text-xs font-medium ${
                          task.completed
                            ? "bg-green-100 text-green-600 dark:bg-green-800 dark:text-green-100"
                            : "bg-orange-100 text-orange-600 dark:bg-orange-800 dark:text-orange-100"
                        }`}
                      />
                    </Box>
                  </Box>
                ))}
                {getTimelineData().length === 0 && (
                  <Typography
                    variant="body2"
                    align="center"
                    className="py-8 text-gray-500 dark:text-gray-400"
                  >
                    No tasks found.
                  </Typography>
                )}
              </Box>
            </Card>
          </Box>
        )}

        {/* Manage Tasks Content */}
        {activeView === "manage-tasks" && (
          <Box className="space-y-6">
            <Box className="flex items-center justify-between mb-6">
              <Typography
                variant="h5"
                className="font-bold text-gray-800 dark:text-gray-100"
              >
                Manage Tasks
              </Typography>
            </Box>
            {/* Filter Tabs for Tasks */}
            <Box className="flex flex-col sm:flex-row sm:items-center space-y-4 sm:space-y-0 sm:space-x-4 mb-6">
              <Typography
                variant="subtitle1"
                className="font-medium text-gray-600 dark:text-gray-300"
              >
                Filter Tasks:
              </Typography>
              <Box className="flex flex-wrap gap-2">
                {[
                  "all",
                  "today",
                  "week",
                  "month",
                  "completed",
                  "pending",
                  "deadline-missed",
                ].map((filter) => (
                  <Button
                    key={filter}
                    onClick={() => handleTimelineClick(filter)}
                    variant={
                      activeTimeline === filter ? "contained" : "outlined"
                    }
                    color={activeTimeline === filter ? "primary" : "inherit"}
                    size="small"
                    sx={{
                      borderRadius: 999,
                      textTransform: "none",
                      "&.MuiButton-outlined": {
                        borderColor: "divider",
                        color: "text.secondary",
                        "&:hover": {
                          backgroundColor: "action.hover",
                        },
                      },
                    }}
                  >
                    {filter.charAt(0).toUpperCase() +
                      filter.slice(1).replace("-", " ")}
                  </Button>
                ))}
                {activeTimeline === "deadline-missed" &&
                  getTimelineData().length > 0 && (
                    <Button
                      onClick={() => {
                        setUsersToEmail(
                          getTimelineData()
                            .map((task) => task.assignedTo)
                            .filter(Boolean)
                        );
                        setShowEmailModal(true);
                      }}
                      variant="contained"
                      color="error"
                      size="small"
                      startIcon={<MailIcon />}
                      sx={{ borderRadius: 999, textTransform: "none" }}
                    >
                      Email Users
                    </Button>
                  )}
              </Box>
            </Box>
            <Card className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700">
              <Typography
                variant="h6"
                className="font-semibold text-gray-800 dark:text-gray-100 mb-4"
              >
                {activeTimeline === "deadline-missed"
                  ? "Deadline Missed Tasks"
                  : "All Tasks"}{" "}
                ({getTimelineData().length})
              </Typography>
              <Box className="space-y-3 max-h-96 overflow-y-auto">
                {getTimelineData().length === 0 ? (
                  <Typography
                    variant="body2"
                    align="center"
                    className="py-8 text-gray-500 dark:text-gray-400"
                  >
                    No tasks found.
                  </Typography>
                ) : (
                  <TableContainer>
                    <Table>
                      <TableHead>
                        <TableRow>
                          <TableCell>Title</TableCell>
                          <TableCell>Assigned To</TableCell>
                          <TableCell>Due Date</TableCell>
                          <TableCell>Priority</TableCell>
                          <TableCell>Status</TableCell>
                          <TableCell align="right">Actions</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {getTimelineData().map((task) => (
                          <TableRow key={task._id}>
                            <TableCell>{task.title}</TableCell>
                            <TableCell>
                              {task.assignedTo?.name || "Unassigned"}
                            </TableCell>
                            <TableCell>
                              {task.dueDate
                                ? format(new Date(task.dueDate), "PPP")
                                : "N/A"}
                            </TableCell>
                            <TableCell>{task.priority}</TableCell>
                            <TableCell>
                              <Chip
                                label={task.completed ? "Completed" : "Pending"}
                                size="small"
                                className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                                  task.completed
                                    ? "bg-green-100 text-green-800 dark:bg-green-800 dark:text-green-100"
                                    : "bg-orange-100 text-orange-800 dark:bg-orange-800 dark:text-orange-100"
                                }`}
                              />
                            </TableCell>
                            <TableCell align="right">
                              {task.assignedTo && (
                                <IconButton
                                  onClick={() =>
                                    handleRemoveTaskAssignment(task._id)
                                  }
                                  color="warning"
                                  title="Unassign Task"
                                >
                                  <UserMinusIcon fontSize="small" />
                                </IconButton>
                              )}
                              <IconButton
                                onClick={() => {
                                  setSelectedTask(task);
                                  setShowTaskModal(true);
                                }}
                                color="info"
                                title="Edit Task"
                              >
                                <EditIcon fontSize="small" />
                              </IconButton>
                              <IconButton
                                onClick={() => handleDeleteTask(task._id)}
                                color="error"
                                title="Delete Task"
                              >
                                <Trash2Icon fontSize="small" />
                              </IconButton>
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                )}
              </Box>
            </Card>
          </Box>
        )}

        {/* Manage Users Content */}
        {activeView === "manage-users" && (
          <Box className="space-y-6">
            <Box className="flex items-center justify-between mb-6">
              <Typography
                variant="h5"
                className="font-bold text-gray-800 dark:text-gray-100"
              >
                Manage Users
              </Typography>
              <Button
                onClick={() => {
                  setSelectedUser(null);
                  setShowUserModal(true);
                }}
                variant="contained"
                color="primary"
                startIcon={<UserPlusIcon />}
              >
                Add New User
              </Button>
            </Box>
            <Card className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700">
              <Typography
                variant="h6"
                className="font-semibold text-gray-800 dark:text-gray-100 mb-4"
              >
                All Users ({users.length})
              </Typography>
              <Box className="space-y-3 max-h-96 overflow-y-auto">
                {users.length === 0 ? (
                  <Typography
                    variant="body2"
                    align="center"
                    className="py-8 text-gray-500 dark:text-gray-400"
                  >
                    No users found.
                  </Typography>
                ) : (
                  <TableContainer>
                    <Table>
                      <TableHead>
                        <TableRow>
                          <TableCell>Name</TableCell>
                          <TableCell>Email</TableCell>
                          <TableCell>Role</TableCell>
                          <TableCell align="right">Actions</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {getFilteredUsers().map((u) => (
                          <TableRow key={u._id}>
                            <TableCell>{u.name}</TableCell>
                            <TableCell>{u.email}</TableCell>
                            <TableCell>
                              <Chip
                                label={u.role}
                                size="small"
                                className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                                  u.role === "admin"
                                    ? "bg-red-100 text-red-800 dark:bg-red-800 dark:text-red-100"
                                    : u.role === "subadmin"
                                    ? "bg-blue-100 text-blue-800 dark:bg-blue-800 dark:text-blue-100"
                                    : "bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-100"
                                }`}
                              />
                            </TableCell>
                            <TableCell align="right">
                              {u.role === "user" && (
                                <IconButton
                                  onClick={() => {
                                    setSubAdminToEdit(u); // Pass the user object to the modal for promotion
                                    setShowSubAdminModal(true);
                                  }}
                                  color="secondary"
                                  title="Make Sub-Admin"
                                >
                                  <UserCheckIcon fontSize="small" />
                                </IconButton>
                              )}
                              {u.role === "subadmin" && (
                                <>
                                  <IconButton
                                    onClick={() => handleDemoteSubAdmin(u._id)}
                                    color="warning"
                                    title="Demote Sub-Admin"
                                  >
                                    <UserMinusIcon fontSize="small" />
                                  </IconButton>
                                  <IconButton
                                    onClick={() => {
                                      setUserToManagePermissions(u);
                                      setShowPermissionModal(true);
                                    }}
                                    color="default"
                                    title="Manage Permissions"
                                  >
                                    <SettingsIcon fontSize="small" />
                                  </IconButton>
                                </>
                              )}
                              <IconButton
                                onClick={() => {
                                  setSelectedUser(u);
                                  setShowUserModal(true);
                                }}
                                color="info"
                                title="Edit User"
                              >
                                <EditIcon fontSize="small" />
                              </IconButton>
                              <IconButton
                                onClick={() => handleDeleteUser(u._id)}
                                color="error"
                                title="Delete User"
                              >
                                <Trash2Icon fontSize="small" />
                              </IconButton>
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                )}
              </Box>
            </Card>
          </Box>
        )}

        {/* Unassigned Users Content */}
        {activeView === "unassigned-users" && (
          <Box className="space-y-6">
            <Typography
              variant="h5"
              className="font-bold text-gray-800 dark:text-gray-100"
            >
              Unassigned Users
            </Typography>
            <Card className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700">
              <Typography
                variant="h6"
                className="font-semibold text-gray-800 dark:text-gray-100 mb-4"
              >
                Users Without Assigned Tasks ({getFilteredUsers().length})
              </Typography>
              <Box className="space-y-3 max-h-96 overflow-y-auto">
                {getFilteredUsers().length === 0 ? (
                  <Typography
                    variant="body2"
                    align="center"
                    className="py-8 text-gray-500 dark:text-gray-400"
                  >
                    All users have tasks assigned!
                  </Typography>
                ) : (
                  <TableContainer>
                    <Table>
                      <TableHead>
                        <TableRow>
                          <TableCell>Name</TableCell>
                          <TableCell>Email</TableCell>
                          <TableCell>Role</TableCell>
                          <TableCell align="right">Actions</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {getFilteredUsers().map((u) => (
                          <TableRow key={u._id}>
                            <TableCell>{u.name}</TableCell>
                            <TableCell>{u.email}</TableCell>
                            <TableCell>
                              <Chip
                                label={u.role}
                                size="small"
                                className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                                  u.role === "admin"
                                    ? "bg-red-100 text-red-800 dark:bg-red-800 dark:text-red-100"
                                    : u.role === "subadmin"
                                    ? "bg-blue-100 text-blue-800 dark:bg-blue-800 dark:text-blue-100"
                                    : "bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-100"
                                }`}
                              />
                            </TableCell>
                            <TableCell align="right">
                              <IconButton
                                onClick={() => {
                                  setSelectedUser(u);
                                  setShowUserModal(true);
                                }}
                                color="info"
                                title="Edit User"
                              >
                                <EditIcon fontSize="small" />
                              </IconButton>
                              <IconButton
                                onClick={() => handleDeleteUser(u._id)}
                                color="error"
                                title="Delete User"
                              >
                                <Trash2Icon fontSize="small" />
                              </IconButton>
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                )}
              </Box>
            </Card>
          </Box>
        )}
      </Box>

      {/* Task Modal (for editing existing tasks) */}
      {showTaskModal && (
        <TaskModal
          task={selectedTask}
          onClose={() => {
            setShowTaskModal(false);
            setSelectedTask(null);
          }}
          onSave={handleSaveTask} // Use the unified save handler
          onDelete={
            selectedTask ? () => handleDeleteTask(selectedTask._id) : undefined
          }
          users={users.filter((u) => u.role === "user")} // Only allow assigning to regular users
        />
      )}
      {/* Task Assign Modal (for creating and assigning NEW tasks from sidebar) */}
      {showTaskAssignModal && (
        <TaskAssignModal
          task={null} // Always null for new task creation
          users={users.filter((u) => u.role === "user")} // Only allow assigning to regular users
          onClose={() => {
            setShowTaskAssignModal(false);
          }}
          onAssign={handleCreateAndAssignTask} // Use the new create and assign handler
        />
      )}
      {/* User Modal */}
      {showUserModal && (
        <UserModal
          user={selectedUser}
          onClose={() => {
            setShowUserModal(false);
            setSelectedUser(null);
          }}
          onSave={selectedUser ? handleUpdateUser : handleCreateUser}
          onDelete={
            selectedUser ? () => handleDeleteUser(selectedUser._id) : undefined
          }
        />
      )}
      {/* SubAdmin Modal */}
      {showSubAdminModal && (
        <SubAdminModal
          userToPromote={subAdminToEdit} // Pass the user object to the modal for promotion/editing
          users={users.filter((u) => u.role === "user")} // Only show regular users for promotion
          onClose={() => {
            setShowSubAdminModal(false);
            setSubAdminToEdit(null);
          }}
          onPromote={handlePromoteToSubAdmin}
          onUpdate={handleUpdateUser} // Re-use update user for sub-admin details
        />
      )}
      {/* Permission Modal */}
      {showPermissionModal && userToManagePermissions && (
        <PermissionModal
          subAdmin={userToManagePermissions}
          users={users.filter((u) => u.role === "user")} // Pass all regular users for specific permissions
          onClose={() => {
            setShowPermissionModal(false);
            setUserToManagePermissions(null);
          }}
          onSave={handleUpdatePermissions}
        />
      )}
      {/* Email Modal */}
      {showEmailModal && (
        <EmailModal
          recipients={usersToEmail}
          onClose={() => setShowEmailModal(false)}
          onSend={handleSendEmail}
        />
      )}
    </Box>
  );
};

export default AdminDashboard;
