"use client";

import { createContext, useContext, useState, useEffect } from "react";
import apiService from "../services/api";

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = apiService.getToken();
    if (token) {
      getCurrentUser();
    } else {
      setLoading(false);
    }
  }, []);

  const getCurrentUser = async () => {
    try {
      const response = await apiService.getCurrentUser();
      if (response.success) {
        setUser(response.user);
      } else {
        logout();
      }
    } catch (error) {
      console.error("Failed to get current user:", error);
      // If the error is due to authentication failure, log out
      if (
        error.message.includes("Authentication failed") ||
        error.message.includes("Unauthorized")
      ) {
        logout();
      }
    } finally {
      setLoading(false);
    }
  };

  const login = async (credentials) => {
    try {
      const response = await apiService.login(credentials);
      if (response.success && response.token) {
        localStorage.setItem("token", response.token); // Store token
        const userResponse = await apiService.getCurrentUser(); // Fetch full user data including permissions
        if (userResponse.success) {
          setUser(userResponse.user);
          return { success: true };
        } else {
          logout(); // If fetching user data fails, log out
          return {
            success: false,
            message:
              userResponse.message || "Failed to fetch user data after login",
          };
        }
      } else {
        return { success: false, message: response.message || "Login failed" };
      }
    } catch (error) {
      return { success: false, message: error.message };
    }
  };

  const register = async (userData) => {
    try {
      const response = await apiService.register(userData);
      if (response.success && response.token) {
        localStorage.setItem("token", response.token); // Store token
        const userResponse = await apiService.getCurrentUser(); // Fetch full user data including permissions
        if (userResponse.success) {
          setUser(userResponse.user);
          return { success: true };
        } else {
          logout(); // If fetching user data fails, log out
          return {
            success: false,
            message:
              userResponse.message ||
              "Failed to fetch user data after registration",
          };
        }
      } else {
        return {
          success: false,
          message: response.message || "Registration failed",
        };
      }
    } catch (error) {
      return { success: false, message: error.message };
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    setUser(null);
    // Redirect to login page after logout
    window.location.href = "/login";
  };

  // Helper functions for role checking
  const isAdmin = () => user?.role === "admin";
  const isSubAdmin = () => user?.role === "subadmin";
  const isUser = () => user?.role === "user";
  const hasRole = (roles) => roles.includes(user?.role);

  // Function to update user state (e.g., after permission changes)
  const refreshUser = async () => {
    await getCurrentUser();
  };

  const value = {
    user,
    login,
    register,
    logout,
    loading,
    isAdmin,
    isSubAdmin,
    isUser,
    hasRole,
    refreshUser, // Expose refreshUser
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
